// old version.
// Calculated at deallocate, convert the pointer subscript into a specific location, and change the specific location of the freelist
// Find the size by which the corresponding block constant's arr pos and arr[pos] should be subtracted
// i is the offset of ptr 0<=i<256 size_class is the page size
pub fn decal_position(i :usize, size_class: usize) -> (usize, u64, usize){
    let mut pos: usize = 0;

    pos = i / 64 ;
    let mut j: usize = i;
    j = j - pos * 64;
    let mut ret: u64 = powers[j];

    (pos, ret, j)
}

// find_block
// parameter i : Indicates which sizeclass the block size to be judged belongs to
// Returns a usize, indicating the sizeclass index to which the block size belongs
pub fn find_block(i: usize) -> usize{
    // If it is a large file, return the subscript of the large file list directly
    if i > 2048 {
        
        return 8;
    }

    return find_block_arr[i];

}
